import { Box, Button, Flex, Image, Link, Text } from "@chakra-ui/react";
import { MdCall } from "react-icons/md";
import logo from "../images/icons/logo.svg";
import logo2 from "../images/icons/bed-svgrepo-com.svg";
import styles from "./Header.module.scss";

export const Header = () => {
  return (
    <header className={styles.container} color={"#2C5282"}>
      <Flex gap={10}>
        <Image src={logo2} alt="" w={40} />

        <Link href="/" className="logo" textDecoration={"none"}>
          <Text color={"#2C5282"} m={0}>
            Абсолют
          </Text>
        </Link>
      </Flex>
      <Flex className="menu" gap={30} alignItems={"center"}>
        <Link
          href="/"
          textDecoration={"none"}
          fontSize={"18px"}
          className="header-link"
        >
          {" "}
          Home
        </Link>
        <Link
          href="/about"
          textDecoration={"none"}
          fontSize={"18px"}
          className="header-link"
        >
          {" "}
          About
        </Link>
        <Link
          href="/rooms"
          textDecoration={"none"}
          fontSize={"18px"}
          className="header-link"
        >
          {" "}
          Rooms
        </Link>
        <Button
          bg={"#BEE3F8"}
          border={0}
          p={"10px 20px"}
          borderRadius={10}
          color={"#2C5282"}
          fontSize={"18px"}
          fontWeight={"700"}
          leftIcon={<MdCall />}
        >
          Contacts
        </Button>
      </Flex>
    </header>
  );
};
