export const Banner = () => {
  return (
    <div className="container">
      <Box bg={"#f7fafd"} w={"70%"} h={500} mt={50} borderRadius={8} p={40}>
        <Text
          as={"h1"}
          fontWeight={800}
          lineHeight={1}
          fontSize={"42px"}
          color="
          #1A365D"
        >
          Абсолют - замечательный хостел для свободных и энергичных
          путешественников
        </Text>
      </Box>
    </div>
  );
};
