import { Box, Button, Flex, Link, Text } from "@chakra-ui/react";
import { MdCall } from "react-icons/md";

export const Header = () => {
  return (
    <header className="container" color={"#2C5282"}>
      <Link href="/" className="logo" textDecoration={"none"}>
        <Text color={"#2C5282"} m={0}>
          Абсолют
        </Text>
      </Link>
      <Flex className="menu" gap={30} alignItems={"center"}>
        <Link
          href="/"
          textDecoration={"none"}
          fontSize={"18px"}
          className="header-link"
        >
          {" "}
          Home
        </Link>
        <Link
          href="/about"
          textDecoration={"none"}
          fontSize={"18px"}
          className="header-link"
        >
          {" "}
          About
        </Link>
        <Link
          href="/rooms"
          textDecoration={"none"}
          fontSize={"18px"}
          className="header-link"
        >
          {" "}
          Rooms
        </Link>
        <Button
          bg={"#BEE3F8"}
          border={0}
          p={"10px 20px"}
          borderRadius={10}
          color={"#2C5282"}
          fontSize={"18px"}
          fontWeight={"700"}
          leftIcon={<MdCall />}
        >
          Contacts
        </Button>
      </Flex>
    </header>
  );
};
